var uid = flow.get("uid") || "";
var battery = flow.get("battery") || -1;
var button = flow.get("button") || 0;
var buzzer = flow.get("buzzer") || 0;
var led0 = flow.get("led0") || 0;
var led1 = flow.get("led1") || 0;
var led2 = flow.get("led2") || 0;
var wifi_signal = flow.get("wifi_signal") || NaN;
var wifi_ssid = flow.get("wifi_ssid") || "";

msg.payload = {
    "device_developer_id": "rpi_01@hadifikri84", // replace with your device developer id
    "data": { 
        "uid":uid,
        "battery":battery, 
        "button":button, 
        "buzzer":buzzer,
        "led0":led0,
        "led1":led1,
        "led2":led2,
        "wifi_signal":wifi_signal,
        "wifi_ssid":wifi_ssid 
    }
}
msg.topic = null;
return msg;

var statusCode = msg.payload.statusCode;
if (statusCode !== 200) {
    msg.payload = {
        "error": msg.payload
    }
    return msg;
}

for (var i = 0; i < msg.payload.results.length; i++) {
    var stream = msg.payload.results[i].data;
    if (stream.hasOwnProperty("type")) {
        // ignore a command
        continue;
    }
    msg.payload = {
        "data": stream,
        "device_developer_id": msg.payload.results[i].device_developer_id,
        "timestamp": new Date(msg.payload.results[i].stream_created_at).toLocaleString()
    };
    break;
}

return msg;
